#include <stdio.h>
#include <string.h>

struct Student {
    char name[50];
    int age;
};

int main() {
    struct Student s1;
    strcpy(s1.name, "John");
    s1.age = 20;
    printf("Name: %s, Age: %d", s1.name, s1.age);
    return 0;
}

